import React, { useContext } from "react";

import { FiSun, FiLogOut } from "react-icons/fi";

import { FaMoon } from "react-icons/fa";

import { GiHamburgerMenu } from "react-icons/gi";

import { IoMdClose } from "react-icons/io";

import { Link, useNavigate } from "react-router-dom";

import Popup from "reactjs-popup";

// import MenuItemsList from "../MenuItemsList";

import ActiveMenuContext from "../../Context/ActiveMenuContext";

import ThemeContext from "../../Context/ThemeContext";

import { removeCookie } from "../../Constants/storageUtilities";

import {
  NavMobileContainer,
  HeaderLogoImg,
  NavMobileIcons,
  IconButton,
  CloseButton,
  NavLargeContainer,
  LogoutPopupContent,
  Button,
  ProfileIcon,
  NavLargeIcons,
  LargeLogoutButton,
  MenuPopupMobile,
  MenuListMobile,
} from "./styledComponents";

const Header: React.FC = () => {
  const navigate = useNavigate();
  const { isDarkTheme, toggleTheme } = useContext(ThemeContext);
  const { changeActiveMenu } = useContext(ActiveMenuContext);

  const onClickLogout = () => {
    removeCookie();
    navigate("/login");
  };

  const renderLogoutPopup = (theme: string) => (
    <Popup
      modal
      trigger={<LargeLogoutButton theme={theme}>Logout</LargeLogoutButton>}
      className="logout-popup"
    >
      {(close) => (
        <div className="modal">
          {" "}
          <button className="close" onClick={close}>
            {" "}
            &times;{" "}
          </button>{" "}
          <div className="header"> Modal Title </div>{" "}
          <div className="content">
            {" "}
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Atque, a
            nostrum. Dolorem, repellat quidem ut, minima sint vel eveniet
            quibusdam voluptates delectus doloremque, explicabo tempore dicta
            adipisci fugit amet dignissimos? <br /> Lorem ipsum dolor sit amet,
            consectetur adipisicing elit. Consequatur sit commodi beatae optio
            voluptatum sed eius cumque, delectus saepe repudiandae explicabo
            nemo nam libero ad, doloribus, voluptas rem alias. Vitae?{" "}
          </div>{" "}
          <div className="actions">
            {" "}
            <Popup
              trigger={<button className="button"> Trigger </button>}
              position="top center"
              nested
            >
              {" "}
              <span>
                {" "}
                Lorem ipsum dolor sit amet, consectetur adipisicing elit. Beatae
                magni omnis delectus nemo, maxime molestiae dolorem numquam
                mollitia, voluptate ea, accusamus excepturi deleniti ratione
                sapiente! Laudantium, aperiam doloribus. Odit, aut.{" "}
              </span>{" "}
            </Popup>{" "}
            <button
              className="button"
              onClick={() => {
                console.log("modal closed ");
                close();
              }}
            >
              {" "}
              close modal{" "}
            </button>{" "}
          </div>{" "}
        </div>
      )}
    </Popup>
  );

  const renderLogo = (theme: string) => (
    <Link to="/">
      <HeaderLogoImg
        src={
          theme === "dark"
            ? "https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-dark-theme-img.png"
            : "https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png"
        }
        alt="website logo"
        onClick={() => changeActiveMenu("HOME")}
      />
    </Link>
  );

  const theme = isDarkTheme ? "dark" : "light";
  const color = isDarkTheme ? "white" : "black";

  return (
    <>
      <NavMobileContainer theme={theme}>
        {renderLogo(theme)}
        <NavMobileIcons>
          <IconButton type="button" data-testid="theme" onClick={toggleTheme}>
            {isDarkTheme ? (
              <FiSun color="white" size={22} />
            ) : (
              <FaMoon size={22} />
            )}
          </IconButton>
          <Popup
            modal
            className="popup-content"
            trigger={
              <IconButton type="button">
                <GiHamburgerMenu color={color} size={22} />
              </IconButton>
            }
          >
            {(close) => (
              <MenuPopupMobile theme={theme}>
                <CloseButton>
                  <IconButton type="button" onClick={close}>
                    <IoMdClose size={20} color={color} />
                  </IconButton>
                </CloseButton>
                <MenuListMobile>
                  <MenuItemsList />
                </MenuListMobile>
              </MenuPopupMobile>
            )}
          </Popup>
          <Popup
            modal
            trigger={
              <IconButton type="button">
                <FiLogOut color={color} size={22} />
              </IconButton>
            }
            className="logout-popup"
          >
            {(
              close: React.MouseEventHandler<HTMLButtonElement> | undefined
            ) => (
              <LogoutPopupContent theme={theme}>
                <p>Are you sure, you want to logout?</p>
                <div>
                  <Button outline type="button" onClick={close}>
                    Cancel
                  </Button>
                  <Button
                    bgColor="blue"
                    color="white"
                    type="button"
                    onClick={onClickLogout}
                  >
                    Confirm
                  </Button>
                </div>
              </LogoutPopupContent>
            )}
          </Popup>
        </NavMobileIcons>
      </NavMobileContainer>
      <NavLargeContainer theme={theme}>
        <Link to="/">
          <HeaderLogoImg
            src={
              isDarkTheme
                ? "https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-dark-theme-img.png"
                : "https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png"
            }
            alt="website logo"
            onClick={() => changeActiveMenu("HOME")}
          />
        </Link>
        <NavLargeIcons>
          <IconButton type="button" onClick={toggleTheme}>
            {isDarkTheme ? (
              <FiSun color="white" size={23} />
            ) : (
              <FaMoon size={23} />
            )}
          </IconButton>
          <ProfileIcon
            src="https://assets.ccbp.in/frontend/react-js/nxt-watch-profile-img.png"
            alt="profile"
          />
          {renderLogoutPopup(theme)}
        </NavLargeIcons>
      </NavLargeContainer>
    </>
  );
};

export default Header;
